$("#botao_cadastro").on('click',function(event){
    event.preventDefault();
    let lista={};
    let id=document.getElementById("id").value;
    let cpf=document.getElementById("cpf_input").value;
    let name=document.getElementById("name_input").value;
    let age=document.getElementById("age_input").value;
    let gender=document.getElementById("gender_input").value;
    let acao=document.getElementById("botao_cadastro").value;

    lista={
        "id" : id,
        "cpf" : cpf, 
        "name" : name,
        "age" : age,
        "gender" : gender,
        "acao" : acao
    }

    $.ajax({
        url: './controllers/ControllerSignUP.php',
        method: 'post',
        data: lista,
        success:function(Dado){
            console.log(Dado)
            $(' .resultado').show().html(Dado);
        }
    })
});

/* Confirmar antes de deletar os dados */
$('.Deletar').on('click',function(event){
    event.preventDefault();

    var Link=$(this).attr('href');

    if(confirm("Deseja mesmo apagar esse dado?")){
        window.location.href=Link;
    }else{
        return false;
    }
});
